import React, { Component } from 'react'
import axios from 'axios'

axios.defaults.headers.common = {
	"Content-Type": "application/json"
  }

export default class TextInput extends Component {

	state={
		code: ""
	}

	onSubmitHandler = (e) => {
		e.preventDefault()
		let data =  {CodeText: 'success'}
		// alert("Submit code")
		axios.post("http://localhost:8080/", querystring.stringify(data))
		.then(response => {
			console.log(response)
		})
		.catch(error => {
			console.log(error)
		})
	}

	OnCodeChangeHandler = (e) => {
		this.setState({
			code: e.target.value
		})
	}

	render() {
		return (
			<div className="container">
				<div className="row">
					<div className="col-12 mt-5">
						<p className="lead d-block my-0" id="writeBelow"> Write below</p>
						<textarea type="text" id="code" value={this.state.code} onChange={this.OnCodeChangeHandler}>
						</textarea>
					</div>
				</div>
				<button className="btn btn-success" id="submitButton" onClick={this.onSubmitHandler}>Submit Code</button>
			</div>
		)

	}
}
